import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';

import Axios from 'axios';
import { useSelector } from 'react-redux';
import { Button } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '40ch',
    },
  },
}));

export default function MultilineTextFields() {

    const { user: currentUser } = useSelector((state) => state.auth);
  const classes = useStyles();
  const [subject, setSubject] = React.useState('');
  
  const [messagetext,setmessagetext]=useState("")

  const handleChange = (event) => {
    setSubject(event.target.value);
  };

  const handleMessageChange =(e)=>{
      setmessagetext(e.target.value);
  }


  const postQuery=()=>{
const username=currentUser.email


date= new Date()
const data=Math.random().toString(36).substring(2, 30) + Math.random().toString(36).substring(2, 30);
console.log(date)
    
  Axios.post('http://localhost:8080/api/postquerymessage',username,subject,messagetext).then(response=>{
      console.log(response)
  })

  }





  return (
    <form className={classes.root} noValidate autoComplete="off">
      <div>
        <TextField
          id="standard-multiline-flexible"
          label="Multiline"
        
          value={subject}
          onChange={handleChange}
        />
        
      </div>
     
      <div>
    
       
        <TextField
          id="outlined-multiline-static"
          label="Multiline"
          multiline
          rows={8}
          defaultValue="Default Value"
          variant="outlined"
          onChange={handleMessageChange}

        />
      </div>



    
     <Button onClick={postQuery}>Send</Button>
   

    </form>
  );
}
