import { Button, List, ListItem, Typography } from '@material-ui/core';
import React, { useState } from 'react'
import { useSelector } from 'react-redux';

export default function Messages(props) {
    // const {response } = useSelector((state) => state.mentormessages);


    // console.log(useSelector((state) => state.mentormessages))




    const [currentmessage, setcurrentmessage] = useState('')

    const responsedata = useSelector((state) => state.mentormessages)

    const [displaymessagelist, setdisplaymessagelist] = useState(false)

const [replybox,setreplybox]=useState(false)

    const messagedata = responsedata.totalmessagedata.response.data

    const [currentIndex, setCurrentIndex] = useState(-1);

    console.log(messagedata)



    const setactivemessage = (messagedata, index) => {

        setcurrentmessage(messagedata)
        setCurrentIndex(index);

        console.log(messagedata, index)


        setdisplaymessagelist(!displaymessagelist)

      




    }


    const GotoBackMenu =()=>{

        setdisplaymessagelist(false)
        setcurrentmessage('')

    }

    const replymessage = ()=>{

        setreplybox(!replybox)

    }

    return (
        <>


        {!(displaymessagelist) ? (<> 



            <List>
                {messagedata &&
                    messagedata.map((messagedata, index) => (


                        <>

                            <ListItem button key={messagedata}
                                className={
                                    "list-group-item " + (index === currentIndex ? "active" : "")

                                }
                                onClick={() => setactivemessage(messagedata, index)}
                                key={index}
                                style={(index === currentIndex ? { backgroundColor: '#b1d9ab' } : null)}


                            >
                                {messagedata.studentemail}
                            </ListItem>
                        </>
                    ))}
            </List>
        
        
        </>) 
        
        
        :( <> </>)}



           


            { currentmessage ? (<>



<Button onClick={GotoBackMenu}>back</Button>

<div style={{flexDirection:'column',display:'flex',justifyContent:'space-evenly'}}>

   <label> From:<Typography color='primary'>{currentmessage.studentemail}</Typography></label>




<label>Subject:<Typography  color='primary'>  {currentmessage.messagesubject}  </Typography> </label>




<label>Date :
<Typography  color='primary'> {currentmessage.createdAt}</Typography></label>


<label>Question
<Typography  color='primary'> {currentmessage.messagetext}</Typography>
</label>



<Button onClick={replymessage}>Reply</Button>

<div>
<TextField
          id="outlined-multiline-static"
          label="Multiline"
          multiline
          rows={8}
          defaultValue="Default Value"
          variant="outlined"
        //   onChange={handleMessageChange}

        />
        </div>





</div>

           



            </>) : (<>


            </>)}


        </>
    )
}
