import React, { useEffect, useState } from 'react'
import { AppBar, Toolbar, Grid, InputBase, IconButton, Badge, makeStyles } from '@material-ui/core'
import NotificationsNoneIcon from '@material-ui/icons/NotificationsNone';
import ChatBubbleOutlineIcon from '@material-ui/icons/ChatBubbleOutline';
import PowerSettingsNewIcon from '@material-ui/icons/PowerSettingsNew';
import SearchIcon from '@material-ui/icons/Search';
import PersonalData from './personaldata'
import Axios from 'axios';
import { useSelector } from 'react-redux';

import { useHistory } from "react-router-dom";

const useStyles = makeStyles(theme => ({
    root: {
        backgroundColor: '#ede102',
     
        
    },
    searchInput: {
        opacity: '0.6',
        padding: `0px ${theme.spacing(1)}px`,
        fontSize: '0.8rem',
        '&:hover': {
            backgroundColor: '#f2f2f2'
        },
        '& .MuiSvgIcon-root': {
            marginRight: theme.spacing(1)
        }
    },

    header:{
        paddingBottom:theme.spacing(10)

    },
    container:{
        
    }
}))

export default function Header(props) {

    let history = useHistory();

    const [notification,setNotification] = useState('')

    const { user: currentUser } = useSelector((state) => state.auth);


    const notificationlength=[]

    useEffect(()=>{

        const mentoremail = currentUser.email
    
        console.log(mentoremail)
    
        Axios.post('http://localhost:8080/api/get/querydataforreply',{mentoremail}).then(response=>{
    
        console.log(response)


       
    

        setNotification(response.data.length)


        })
    
      },[])

     

      const logout =()=>{

        localStorage.clear()
          history.push("/login")
        
      }



    const classes = useStyles();

    return (

        <div>

            <div className={classes.header}>
        <AppBar position="fixed" className={classes.root}>
            <Toolbar>
                <Grid container
                    alignItems="center">
                    <Grid item>
                        <InputBase
                            placeholder="Search topics"
                            className={classes.searchInput}
                            startAdornment={<SearchIcon fontSize="small" />}
                        />
                    </Grid>
                    <Grid item sm></Grid>
                    <Grid item>
                        <IconButton>
                            <Badge badgeContent={notification} color="secondary">
                                <NotificationsNoneIcon fontSize="small" />
                            </Badge>
                        </IconButton>
                        <IconButton>
                            <Badge badgeContent={notification} color="primary">
                                <ChatBubbleOutlineIcon fontSize="small" />
                            </Badge>
                        </IconButton>
                        <IconButton  onClick={logout}>
                            <PowerSettingsNewIcon fontSize="small" />
                        </IconButton>
                    </Grid>
                </Grid>
            </Toolbar>
        </AppBar>

        </div>

        
  <div >

  <PersonalData />

  </div>
        

        </div>
    )
}
